package br.com.exercicios02;



public class Exercicio13 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num = 3;
		
		for (int i = 0; i <= 3 ; i++) {
			for (int j = 0; j <= 10; j++) {
				System.out.println(num +" x "+ j +" = "+ (num*j));
			}
			num++;
		}
	}
	
	
	

}
